import React, { useState, useEffect, useContext } from 'react';
import createAuth0Client from '@auth0/auth0-spa-js';

// create a context to hold the authentication state
const Auth0Context = React.createContext();

// create a provider to wrap your app or relevant components
const Auth0Provider = ({ children }) => {
  const [auth0Client, setAuth0Client] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const initAuth0 = async () => {
      const auth0 = await createAuth0Client({
        domain: 'dev-fxy2vqxz8ras4kez.us.auth0.com',
        client_id: 'qDhNOvieJZB5srLUlfpAeI4cNQ4FVIPw',
        redirect_uri: window.location.origin,
      });
      setAuth0Client(auth0);

      if (window.location.search.includes('code=')) {
        const { appState } = await auth0.handleRedirectCallback();
        window.history.replaceState({}, document.title, appState && appState.targetUrl ? appState.targetUrl : window.location.pathname);
      }

      const isAuthenticated = await auth0.isAuthenticated();
      setIsAuthenticated(isAuthenticated);

      if (isAuthenticated) {
        const user = await auth0.getUser();
        setUser(user);
      }
    };

    initAuth0();
  }, []);

  const login = async () => {
    await auth0Client.loginWithRedirect();
  };

  const logout = () => {
    auth0Client.logout({ returnTo: window.location.origin });
  };

  return (
    <Auth0Context.Provider
      value={{
        isAuthenticated,
        user,
        login,
        logout,
      }}
    >
      {children}
    </Auth0Context.Provider>
  );
};

// create a hook to access the authentication context
const useAuth0 = () => useContext(Auth0Context);

export { Auth0Provider, useAuth0 };
