import React, { useState, createContext } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const { logout: auth0Logout, isAuthenticated: auth0IsAuthenticated, loginWithRedirect: auth0LoginWithRedirect } = useAuth0();
  const [isAuthenticated, setIsAuthenticated] = useState(auth0IsAuthenticated);

  const logout = () => {
    auth0Logout();
    setIsAuthenticated(false);
  };

  const loginWithRedirect = () => {
    auth0LoginWithRedirect();
  };

  return (
    <AuthContext.Provider value={{ logout, isAuthenticated, loginWithRedirect }}>
      {children}
    </AuthContext.Provider>
  );
};
