import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';

const Contact = () => {
  const handleSubmit = (values, actions) => {
    // Send form data to email here
    console.log(values);
    actions.setSubmitting(false);
  };

  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Name is required'),
    email: Yup.string().email('Invalid email address'),
    subject: Yup.string().required('Subject is required'),
    message: Yup.string().email('Message is required'),
  });

  return (
    <div className="h-min px-[10%] mx-[8.5%] pt-[10%] pb-10">
          <div className="text-4xl font-bold my-7">Contact</div>
          <div>
              <Formik
                  initialValues={{
                      name: '',
                      email: '',
                      subject: '',
                      message: '',
                  }}
                  validationSchema={validationSchema}
                  onSubmit={handleSubmit}
              >
                  {({ isSubmitting }) => (
                      <Form className="bg-white rounded-lg mb-10">
                          <div className="mb-4">
                              <label className="block text-gray-700 font-medium mb-2" htmlFor="name">
                                  Your Name*
                              </label>
                              <Field
                                  className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                  name="name"
                                  placeholder="Your Name*" />
                          </div>
                          <div className="mb-4">
                              <label className="block text-gray-700 font-medium mb-2" htmlFor="phone">
                                  Your Email*
                              </label>
                              <Field
                                  className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                  name="email"
                                  placeholder="Your Email*" />
                          </div>
                          <div className="mb-4">
                              <label className="block text-gray-700 font-medium mb-2" htmlFor="address">
                                  Subject*
                              </label>
                              <Field
                                  className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                  name="subject"
                                  placeholder="Subject*" />
                          </div>
                          <div className="mb-4">
                              <label className="block text-gray-700 font-medium mb-2" htmlFor="workExperience">
                                  Message*
                              </label>
                              <Field
                                  className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"
                                  name="message"
                                  placeholder="Message*"
                                  component="textarea" />
                          </div>
                          <button
                              className=" bg-[#04147C] text-white py-2 px-4 rounded-lg hover:bg-blue-700"
                              type="submit"
                          >
                              Submit
                          </button>
                      </Form>
                  )}
              </Formik>
          </div>
      </div>
  );
};

export default Contact;
