import React, { useState } from 'react';


export default function NavBar() {
    const [navbar, setNavbar] = useState(false);
    const [currentIndex, setCurrentIndex] = useState(0);

    const images = [
        { url: './path/to/download-1.jpg', text: 'First Slide' },
        { url: 'https://flowbite.com/docs/images/carousel/carousel-2.svg' },
        { url: 'https://flowbite.com/docs/images/carousel/carousel-3.svg' },
    ];

    const handlePrevClick = () => {
        if (currentIndex === 0) {
          setCurrentIndex(images.length - 1);
        } else {
          setCurrentIndex(currentIndex - 1);
        }
    };

    const handleNextClick = () => {
        if (currentIndex === images.length - 1) {
          setCurrentIndex(0);
        } else {
          setCurrentIndex(currentIndex + 1);
        }
    };

    const handleSlideClick = (index) => {
        setCurrentIndex(index);
    };


    return (
        <><div className="bg-cover h-screen" style={{ backgroundImage: `url(${process.env.PUBLIC_URL + '/download-1.jpg'})` }}>
            <nav className="fixed top-0 w-full bg-white shadow">
                <div className="justify-between px-4 mx-auto lg:max-w-7xl md:items-center md:flex md:px-8">
                    <div>
                        <div className="flex items-center justify-between py-3 md:py-5 md:block">
                            <a href="/">
                                <img class="shrink w-25 h-20 sm:w-15 h-15" src="/logo.png" alt="Workflow" />
                            </a>
                            <div className="md:hidden">
                                <button
                                    className="p-2 text-gray-700 rounded-md outline-none focus:border-gray-400 focus:border"
                                    onClick={() => setNavbar(!navbar)}
                                >
                                    {navbar ? (
                                        <svg
                                            xmlns="http://www.w3.org/2000/svg"
                                            className="w-6 h-6"
                                            viewBox="0 0 20 20"
                                            fill="currentColor"
                                        >
                                            <path
                                                fillRule="evenodd"
                                                d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                                                clipRule="evenodd" />
                                        </svg>
                                    ) : (
                                        <svg
                                            xmlns="http://www.w3.org/2000/svg"
                                            className="w-6 h-6"
                                            fill="none"
                                            viewBox="0 0 24 24"
                                            stroke="currentColor"
                                            strokeWidth={2}
                                        >
                                            <path
                                                strokeLinecap="round"
                                                strokeLinejoin="round"
                                                d="M4 6h16M4 12h16M4 18h16" />
                                        </svg>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                    <div>
                        <div
                            className={`flex-1 justify-self-center pb-3 mt-8 md:block md:pb-0 md:mt-0 ${navbar ? "block" : "hidden"}`}
                        >
                            <ul className="items-center justify-center space-y-8 md:flex md:space-x-6 md:space-y-0">
                                <li className="text-[#04147C] hover:text-blue-600 text-xl font-semibold">
                                    <a href="javascript:void(0)">Home</a>
                                </li>
                                <li className="text-[#04147C] hover:text-blue-600 text-xl font-semibold">
                                    <a href="javascript:void(0)">Blog</a>
                                </li>
                                <li className="text-[#04147C] hover:text-blue-600 text-xl font-semibold">
                                    <a href="javascript:void(0)">About</a>
                                </li>
                                <li className="text-[#04147C] hover:text-blue-600 text-xl font-semibold">
                                    <a href="javascript:void(0)">Contact</a>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </nav>
        </div><div className="h-screen flex justify-center">
            <div className="text-2xl font-semibold h-screen w-1/2 grid content-center">
                <div className="flex justify-center text-5xl font-bold mx-20">
                    Building infrastructure that lasts for over 70 years
                </div>
            </div>
            <div className="text-2xl font-semibold h-screen w-1/2 grid content-center">
                <div className="flex justify-center">
                    <div className="relative">
                        <div className="overflow-hidden relative h-56 rounded-lg sm:h-64 xl:h-80 2xl:h-96">
                            {images.map((image, index) => (
                            <div
                                key={index}
                                className={`hidden duration-700 ease-in-out ${index === currentIndex ? 'block' : 'hidden'}`}
                            >
                                {image.text && (
                                <span className="absolute top-1/2 left-1/2 text-2xl font-semibold text-white -translate-x-1/2 -translate-y-1/2 sm:text-3xl dark:text-gray-800">
                                    {image.text}
                                </span>
                                )}
                                <img
                                src={image.url}
                                className="block absolute top-1/2 left-1/2 w-full -translate-x-1/2 -translate-y-1/2"
                                alt="slide"
                                />
                            </div>
                            ))}
                        </div>
                        <div className="flex absolute bottom-5 left-1/2 z-30 space-x-3 -translate-x-1/2">
                            {images.map((_, index) => (
                            <button
                                type="button"
                                className={`w-3 h-3 rounded-full ${index === currentIndex ? 'bg-indigo-500' : 'bg-gray-500'}`}
                                aria-current={index === currentIndex}
                                aria-label={`Slide ${index + 1}`}
                                onClick={() => handleSlideClick(index)}
                                key={index}
                            />
                            ))}
                        </div>
                        <button
                            type="button"
                            className="flex absolute top-0 left-0 z-30 justify-center items-center px-4 h-full cursor-pointer group focus:outline-none"
                            onClick={handlePrevClick}
                            >
                            <span className="inline-flex justify-center items-center w-8 h-8 rounded-full sm:w-10 sm:h-10 bg-white/30 dark:bg-gray-800/30 group-hover:bg-white/50 dark:group-hover:bg-gray-800/60 group-focus:ring-4 group-focus:ring-white dark:group-focus:ring-gray-800/70 group-focus:outline-none">
                                <svg
                                className="w-5 h-5 text-white sm:w-6 sm:h-6 dark:text-gray-800"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                                xmlns="http://www.w3.org/2000/svg"
                                >
                                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7"></path>
                                </svg>
                                <span className="hidden">Previous</span>
                            </span>
                        </button>
                        <button
                            type="button"
                            className="flex absolute top-0 right-0 z-30 justify-center items-center px-4 h-full cursor-pointer group focus:outline-none"
                            onClick={handleNextClick}
                            >
                            <span className="inline-flex justify-center items-center w-8 h-8 rounded-full sm:w-10 sm:h-10 bg-white/30 dark:bg-gray-800/30 group-hover:bg-white/50 dark:group-hover:bg-gray-800/60 group-focus:ring-4 group-focus:ring-white dark:group-focus:ring-gray-800/70 group-focus:outline-none">
                                <svg
                                className="w-5 h-5 text-white sm:w-6 sm:h-6 dark:text-gray-800"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                                xmlns="http://www.w3.org/2000/svg"
                                >
                                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7"></path>
                                </svg>
                                <span className="hidden">Next</span>
                            </span>
                        </button>
                    </div>
                </div>
            </div>    
        </div></>
    );
}
